const initialHousesState = {
    houses : [

    ]
}


export const addHouseDetails = (state = initialHousesState, action) => {
    switch(action.type) {
        case "ADD_HOUSES" : 

            action.payload.array.forEach(element => {
                state.houses.push({ name : element.name, id: element.id, tree : {}})
            });
            return state;

         case "ADD_HOUSE_FAMILY_TREE" : 
            state.houses.filter(element => element.name == action.payload.houseName)[0].tree = action.payload.tree;
            return state;

        case "addChild" : 
            return {...state,child: state.child + action.payload}

        case "removeChild" : 
            return {...state,child: state.child - action.payload}

        default : 
            return state
    }
}
