import React, { useEffect } from 'react'
import { addHouses } from '../redux/actions/HouseActions'
import { GetRequests } from '../util/ApiUtils'
import { useDispatch, useSelector } from 'react-redux'
import { houseDetails } from '../redux/reducers/HouseReducers'
import {
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Box,
} from '@chakra-ui/react'

import FamilyTreeComponent from "../components/FamilyTreeComponent"

function HomePage() {

  const dispatch = useDispatch();

  useEffect(() => {
    GetRequests("/api/characters/houses").then(res => {
      console.log("Response ", res);
      dispatch(addHouses(res.data))
    });

  }, [])

  const houses = useSelector((state) => state.houseDetails.houses)
  console.log("House details  ", houses);
  return (
    <div>
      <Accordion>

        {houses.map((element, i) => (
          <AccordionItem>
            <h2>
              <AccordionButton>
                <Box as="span" flex='1' textAlign='left'>
                  {element.name}
                </Box>
                <AccordionIcon />
              </AccordionButton>
            </h2>
            <AccordionPanel pb={i}>
              <FamilyTreeComponent />
            </AccordionPanel>
          </AccordionItem>
        ))}

      </Accordion>
    </div>
  )
}

export default HomePage
