import { ArcherContainer, ArcherElement } from "react-archer";
import data from "../FamilyTreeComponent/response.json";

const rootStyle = { display: "flex", justifyContent: "center" };
const rowStyle = {
    margin: "200px 0",
    display: "flex",
    justifyContent: "space-between"
};

const boxStyle = { padding: "10px", border: "1px solid black" };


const constructTree = (data) => {

    var targetObjects = [];
    data.children.map((element, i) => targetObjects.push({ kind: "child", targetId : element.characterName, targetAnchor: "top", sourceAnchor: "bottom", style: { strokeDasharray: "5,5" }}))
    data.spouse.map((element, i) => targetObjects.push({ kind: "spouse", targetId : element.characterName, targetAnchor: "left", sourceAnchor: "right", style: { strokeColor: "blue", strokeWidth: 1 }}))
    console.log("Target Objects ",targetObjects);
    return(
        <div>
            <div key={data.characterName} style={rootStyle}>

                    <ArcherElement
                        id={data.characterName}
                        key={data.characterName}
                        relations={targetObjects}

                    >
                        <div style={boxStyle}>{data.characterName}</div>
                    </ArcherElement>
                    {
                        data.spouse.map((element, i) => (
                            <ArcherElement
                                id={data.characterName}
                                key={data.characterName}
                                //relations={targetObjects}

                            >
                                <div style={boxStyle}>{element.characterName}</div>
                            </ArcherElement>
                        ))
                    }

            </div>
            <div style={rowStyle}>
                {
                    data.children.map((element, i) => (
                        constructTree(element)
                    ))
                }
            </div>
        </div>
    )

}

