import { Box, Flex } from '@chakra-ui/react'
import React from 'react'
import ArrowDomComponent from '../ArrowDomComponent'
import { ArcherContainer, ArcherElement } from "react-archer";
import Queue from "../../libraries/Queue"
import CardComponent from '../CardComponent'


const rootStyle = { margin: "20px 0px", display: "flex", justifyContent: "center" };
const rowStyle = {
    margin: "100px 0",
    display: "flex",
    justifyContent: "space-between"
};

const boxStyle = { padding: "10px", border: "1px solid black", display: "flex", justifyContent: "space-between" };

const constructTargetObjects = (data) => {
    var targetObjects = [];
    data.children.map((element, i) => targetObjects.push({ targetId : element.characterName, targetAnchor: "top", sourceAnchor: "bottom", style: { strokeDasharray: "5,5" }}))
    data.spouse.map((element, i) => targetObjects.push({ targetId : element.characterName, targetAnchor: "left", sourceAnchor: "right", style: { strokeColor: "blue", strokeWidth: 1 }}))

    return targetObjects;
}

const constructRelations = (data) => {
    var relations = [];
    if(data.parents.length>0) relations.push({key: "Parents", value: data.parents.toString()})
    if(data.siblings.length>0) relations.push({key: "Siblings", value: data.siblings.toString()})
    if(data.killed.length>0) relations.push({key: "Killed", value: data.killed.toString()})
    if(data.killedBy.length>0) relations.push({key: "Killed by", value: data.killedBy.toString()})
    if(data.guardianOf.length>0) relations.push({key: "Guardian of", value: data.guardianOf.toString()})
    if(data.guardedBy.length>0) relations.push({key: "Guarded by", value: data.guardedBy.toString()})

    return relations;
}


const constructTree = (props) => {

    let headNode = props.data
    let q = new Queue();

    var treeTraversed = []
    q.enqueue(headNode)
    headNode.spouse.map((node, i) => headNode.enqueue(node));
    q.enqueue(null);
    var level = 1;
    while(!q.isEmpty() && q.peek()!=null){

        let currentLevelNodes = []
        let currentNode = q.dequeue();

        while(currentNode!=null){

            let relations = constructRelations(currentNode)
            currentLevelNodes.push({ node: currentNode, mapping: constructTargetObjects(currentNode), relations: relations})

            currentNode.children.map((node, i) => {
                q.enqueue(node)
                node.spouse.map((spouseNode, i) => q.enqueue(spouseNode))
            })

            currentNode = q.dequeue();

        }

        q.enqueue(null);
        level = level+1;
        treeTraversed.push(currentLevelNodes)


    }

    return (
        <ArcherContainer strokeColor="red">

            {



                treeTraversed.map((levelTraversed, i) => (

                    i==0 ? 

                    <div style={rootStyle}>
                    {levelTraversed.map((nodeObject, i)=> (
                        <ArcherElement
                        id={nodeObject.node.characterName}
                        key={nodeObject.node.characterName}
                        relations={nodeObject.mapping}

                        >
                            <div style={boxStyle}>{nodeObject.node.characterName}</div>
                        </ArcherElement>
                    ))}
                    </div>

                    : 

                    <div style={rowStyle}>
                    {levelTraversed.map((nodeObject, i)=> (
                        <ArcherElement
                        id={nodeObject.node.characterName}
                        key={nodeObject.node.characterName}
                        relations={nodeObject.mapping}

                        >
                            {/*<div style={boxStyle}>{nodeObject.node.characterName} <img src={filledHeart} alt="hollowHeart" height={20} width={20} style={{marginLeft : 10}}/></div>*/}
                            <Box> <CardComponent element={nodeObject} fetchFamilyTreeData={props.fetchFamilyTreeData} /> </Box>
                        </ArcherElement>
                    ))}
                    </div>
                ))
            }

        </ArcherContainer>
    )

}


function FamilyTreeComponent(props) {

  return (
    <Box maxWidth='4000px' >
                {constructTree(props)}
    </Box>
  )
}

export default FamilyTreeComponent
