import { Box, Card, CardBody, CardHeader, Heading, StackDivider, Text, Stack, Center } from '@chakra-ui/react'
import React from 'react'
import hollowHeart from '../../assets/hollow_heart.png'
import filledHeart from '../../assets/filled_heart.png'
function CardComponent(props) {

    console.log("In card component ", props.element);

  return (
    <Card maxWidth="200px" alignContent='center'>
        <CardHeader style={{display: "flex", justifyContent: "space-between"}}>
            <Heading size='sm'>{props.element.characterName}</Heading>
            { props.element.favorite == false ? 
                <img src={hollowHeart} alt="hollowHeart" height={20} width={20} style={{marginLeft : 10}}/>
                :
                <img src={filledHeart} alt="filled" height={20} width={20} style={{marginLeft : 10}}/>
            }
        </CardHeader>

        <CardBody>
            <Stack divider={<StackDivider />} spacing='4'>

                {props.element.parents.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Parents : {props.element.parents.toString()}
                    </Text>
                </Box> : null}

                {props.element.siblings.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Siblings : {props.element.siblings.toString()}
                    </Text>
                </Box> : null}

                {props.element.guardianOf.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Guardian of : {props.element.guardianOf.toString()}
                    </Text>
                </Box> : null}

                {props.element.guardedBy.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Guarded by : {props.element.guardedBy.toString()}
                    </Text>
                </Box> : null}

                {props.element.killed.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Killed : {props.element.killed.toString()}
                    </Text>
                </Box> : null}

                {props.element.killedBy.length>0 ? <Box>
                    <Text pt='2' fontSize='sm'>
                        Killed by : {props.element.killedBy.toString()}
                    </Text>
                </Box> : null}

            </Stack>
        </CardBody>
    </Card>
  )
}

export default CardComponent
