import React, { useState } from 'react'
import {
    AccordionItem,
    AccordionButton,
    AccordionPanel,
    AccordionIcon,
    Box,
  } from '@chakra-ui/react'

import FamilyTreeComponent from '../FamilyTreeComponent'
import { GetRequests } from '../../util/ApiUtils';

function HouseListItem(props) {

  const [familyTree, setFamilyTree] = useState({characterName: "#", spouse : [], children:[]});

  const onAccordianExpanded = () => {

    GetRequests("/api/characters/familytree/"+props.element.name).then(response => {
      setFamilyTree(response.data)
    })

  }

  //console.log("FamilyTree in HouseListItem");

  return (
    <AccordionItem>
        <h2>
            <AccordionButton onClick={onAccordianExpanded}>
            <Box as="span" flex='1' textAlign='left'>
                {props.element.name}
            </Box>
            <AccordionIcon />
            </AccordionButton>
        </h2>
        <AccordionPanel pb={props.i}>
        <Box
            overflowY="auto"
            css={{
                '&::-webkit-scrollbar': {
                width: '4px',
                },
                '&::-webkit-scrollbar-track': {
                width: '6px',
                },
                '&::-webkit-scrollbar-thumb': {
                scrollbarColor: 'red green',
                borderRadius: '24px',
                },
            }}
            ><FamilyTreeComponent data={familyTree}/></Box>
        </AccordionPanel>
    </AccordionItem>
  )
}

export default HouseListItem
