const constructTree = (headNode) => {

    let q = new Queue();

    var treeTraversed = []
    q.enqueue(headNode)
    headNode.spouse.map((node, i) => headNode.enqueue(node));
    q.enqueue(null);
    var level = 1;
    while(!q.isEmpty() && q.peek()!=null){

        let currentLevelNodes = []
        console.log("current nodes ", currentLevelNodes);
        let currentNode = q.dequeue();
        while(currentNode!=null){

            currentLevelNodes.push({ node: currentNode, mapping: constructTargetObjects(currentNode)})

            //console.log(currentNode.characterName);
            currentNode.children.map((node, i) => {
                q.enqueue(node)
                node.spouse.map((spouseNode, i) => q.enqueue(spouseNode))
            })

            currentNode = q.dequeue();

        }

        q.enqueue(null);
        level = level+1;
        treeTraversed.push(currentLevelNodes)


    }

    console.log("Current level Nodes ", treeTraversed);

    return (
        <div></div>
    )

}


